import React, { useState } from "react";

import { LikeFilled,
    CommentOutlined,
    LikeOutlined, 
    MoreOutlined} from '@ant-design/icons';

import { Image,
    Card,
    Dropdown,
    } from "react-bootstrap";

import Toaster from "../Toaster";
import { randomAvatar } from "../../utils";
import axiosService from "../../helpers/axios";
import { getUser } from "../../hooks/user.actions";

import UpdatePost from "./UpdatePost";


const MoreToggleIcon = React.forwardRef(({ onClick }, ref) => (

    <a
    href = "#"
    ref = {ref}
    onClick = {(e) => {
        e.preventDefault();
        onClick(e);
    }}
    >
        <MoreOutlined />
    </a>
));


function Post (props) {
    //The refresh function will come from SWR posts.
    //SWR return an object with a mutate method that
    //can be used to trigger the fetching of data.
    const { post, refresh } = props;

    //Two actions can be passed to this function.
    //like or remove_like.
    //If the request succeeds, we refresh the posts.
    const handleLikeClick = (action) => {
        axiosService.post(`/post/${post.id}/${action}/`)
        .then(() => {
            refresh();
        })
        .catch((err) => console.error(err));
    };

    const [ showToast, setShowToast ] = useState(false);
    const user = getUser();

    const handleDelete = () => {
        axiosService.delete(`/post/${post.id}/`)
        .then(() => {
            setShowToast(true);
            refresh();
        })
        .catch((err) => console.error(err));
    }

    return (
        <>
            <Card className = "rounded-3 my-4">

                <Card.Body>

                    <></>

                    <Card.Title
                    className = "d-flex flex-row justify-content-between">

                        <div className = "d-flex flex-row">
                            <Image
                            src = {randomAvatar()}
                            roundedCircle
                            width = {48}
                            height = {48}
                            className = "me-2 border border-primary border-2"/>

                            <div
                            className = {"d-flex " +
                                        "flex-column " +
                                        "justify-content-start" +
                                        " align-self-center mt-2"}>
                                
                                <p className = "fs-6 m-0">
                                    {post.author.first_name} {post.author.last_name}
                                </p>

                                <p className = "fs-6 fw-leighter">
                                    <small>
                                        {post.created}
                                    </small>
                                </p>
                            </div>
                        </div>

                        {console.log('user.name: ', user.name, ' ', 'post.author.name: ', post.author.name)}
                        {console.log('user: ', user, ' ', 'post: ', post)}


                        {user.name === post.author.name && (
                            <div>
                                <Dropdown>
                                    <Dropdown.Toggle as = {MoreToggleIcon}>
                                    </Dropdown.Toggle>
                                    <Dropdown.Menu>
                                            
                                            <UpdatePost
                                            post = {post}
                                            refresh = {refresh} />
                                            
                                        <Dropdown.Item
                                        onClick = {handleDelete}
                                        className = "text-danger">
                                            Delete
                                        </Dropdown.Item>
                                    </Dropdown.Menu>
                                </Dropdown>
                            </div>
                        )}


                    </Card.Title>


                    <Card.Text>
                        {post.body}
                    </Card.Text>


                    <div className = "d-flex flex-row">
                        <LikeFilled
                        style = {{
                            color: "#fff",
                            backgroundColor: "#0D6EFD",
                            borderRadius: "50%",
                            width: "18px",
                            height: "18px",
                            fontSize: "75%",
                            padding: "2px",
                            margin: "3px",
                        }}/>

                        <p className = "ms-1 fs-6">
                            <small>
                                {post.likes_count} like
                            </small>
                        </p>
                    </div>
                </Card.Body>


                <Card.Footer className = {"d-flex bg-white w-50 " +
                                            "justify-content-between "+
                                            "border-0"
                }>
                    <div className = "d-flex flex-row">
                        <LikeOutlined 
                        style = {{
                            width: "24px",
                            height: "24px",
                            padding: "2px",
                            fontsize: "20px",
                            color: post.liked ? "#0D6EFD" : "#C4C4C4",
                        }}
                        onClick = {() => {
                            if (post.liked) {
                                handleLikeClick("remove_like");
                            } else {
                                handleLikeClick("like");
                            }
                        }}
                        />
                        <p className = "ms-1">
                            <small>
                                Like
                            </small>
                        </p>
                    </div>

                    <div className = "d-flex flex-row">
                        <CommentOutlined
                        style = {{
                            width: "24px",
                            height: "24px",
                            padding: "2px",
                            fontSize: "20px",
                            color: "#C4C4C4",
                        }}/>

                        <p className = "ms-1 mb-0">
                            <small>
                                Comment
                            </small>
                        </p>
                    </div>
                </Card.Footer>

            </Card>

            <Toaster
            title = "Post!"
            message = "Post deleted"
            type = "danger"
            showToast = {showToast}
            onClose = {() => setShowToast(false)}/>

        </>
    );
}

export default Post;
