import React from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/Layouts";
import ProfileDetails from "../components/profile/ProfileDetail";
import useSWR from "swr";
import { fetcher } from "../helpers/axios";
import Post from "../components/posts/Post";
import { Row, Col } from "react-bootstrap";

function Profile() {
    const { profileId } = useParams();
    console.log('Profile, profileId: ', profileId);


    const user = useSWR(`/user/${profileId}/`, fetcher);
    console.log('Profile, user: ', user);

    const posts = useSWR(`/post/?author__public_id=${profileId}`,
                            fetcher,
                            {refreshInterval: 20000,});

    return (
        <Layout hasNavigationBack>
            <Row className="justify-content-evenly">
                <Col sm={9}>
                    <ProfileDetails user={user.data} />
                        <div>
                            <Row className="my-4">
                                {posts.data?.results.map((post, index) => (
                                <Post key={index} post={post} refresh={posts.mutate} />
                                ))}
                            </Row>
                        </div>
                </Col>
            </Row>
        </Layout>
    );
}

export default Profile;
