import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";


function RegistrationForm(){
    const navigate = useNavigate();//Will help us navigate to the home
                                   // page if the request is successful
    //Used to check wether the form is valid or not.
    const [validated, setValidated] = useState(false);
    const [form, setForm] = useState({});
    const [error, setError] = useState(null);

    const hadleSubmit = (event) => {
        //Blocking the default form submission behavior
        //that is, reloading the page.
        event.preventDefault();

        const RegistrationForm = event.currentTarget;

        //Check whether the basic validations for the 
        //fields are done.
        if(RegistrationForm.checkValidity() === false) {
            event.stopPropagation();
        }

        //If the basic validations are done...
        setValidated(true);

        const data = {
            username: form.username,
            password: form.password,
            email: form.email,
            first_name: form.first_name,
            last_name: form.last_name,
            bio: form.bio,
        };

        axios.post("http://localhost:8000/api/auth/register/", data)
        .then((res) => {
            localStorage.setItem("auth", JSON.stringify({
                access: res.data.access,
                refresh: res.data.refresh,
                user: res.data.user,
            }));

            navigate("/");
        })
        .catch((err) => {
            if (err.message){
                setError(err.request.response);
            }
        })
    };

    //Adding the basic UI components
    return (
        <Form
        id = "registration-form"
        className = "border p-4 rounded"
        noValidate
        validated = {validated}
        onSubmit = {hadleSubmit}>
                
            <Form.Group className = "mb-3">

                <Form.Label>First Name</Form.Label>
                <Form.Control
                value = {form.first_name}
                onChange = 
                {(e) => setForm({ 
                    ...form,
                    first_name: e.target.value})
                }
                required
                type = "text"
                placeholder = "Enter first name" />
                <Form.Control.Feedback type = "invalid">
                    This filed is required.
                </Form.Control.Feedback>

            </Form.Group>

            
            <Form.Group className="mb-3">
                <Form.Label>Last name</Form.Label>
                <Form.Control
                value={form.last_name}
                onChange={(e) => 
                    setForm({ ...form, last_name: e.target.value })}
                required
                type="text"
                placeholder="Enter last name"
                />
                <Form.Control.Feedback type="invalid">
                    This file is required.
                </Form.Control.Feedback>
            </Form.Group>


            <Form.Group className="mb-3">
                <Form.Label>Username</Form.Label>
                <Form.Control
                value={form.username}
                onChange={(e) => 
                    setForm({ ...form, username: e.target.value })}
                required
                type="text"
                placeholder="Enter username"
                />
                <Form.Control.Feedback type="invalid">
                    This file is required.
                </Form.Control.Feedback>
            </Form.Group>


            <Form.Group className="mb-3">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                value={form.email}
                onChange={(e) => 
                    setForm({ ...form, email: e.target.value })}
                required
                type="email"
                placeholder="Enter email"
                />
                <Form.Control.Feedback type="invalid">
                    Please provide a valid email.
                </Form.Control.Feedback>
            </Form.Group>



            <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <Form.Control
                value={form.password}
                minLength="8"
                onChange={(e) => 
                    setForm({ ...form, password: e.target.value })}
                required
                type="password"
                placeholder="Password"
                />
                <Form.Control.Feedback type="invalid">
                    Please provide a valid password.
                </Form.Control.Feedback>
            </Form.Group>



            <Form.Group className="mb-3">
                <Form.Label>Bio</Form.Label>
                <Form.Control
                value={form.bio}
                onChange={(e) =>
                    setForm({ ...form, bio: e.target.value })}
                as="textarea"
                rows={3}
                placeholder="A simple bio ... (Optional)"
                />
            </Form.Group>

            <div className = "text-content text-danger">
                {error && <p>{error}</p>}
            </div>

            <Button variant = "primary" type = "submit">
                Submit
            </Button>


        </Form>
    );
}

export default RegistrationForm;
